mapper.select:
select m.member_name, p.id, p.post_content, p.post_title, p.created_date, p.updated_date, p.member_id, p.post_status, p.category_id from tbl_member m join tbl_post p on m.id = p.member_id join tbl_category c on p.category_id = c.id where p.post_status = 'active'
and ( p.post_title like concat('%', #{keyword}, '%') or p.post_content like concat('%', #{keyword}, '%') or m.member_name like concat('%', #{keyword}, '%') )
order by p.id desc limit #{postCriteriaDTO.postCriteria.count} offset #{postCriteriaDTO.postCriteria.offset}
