mapper.select:
select id, member_name, member_email, kakao_email, member_provider, member_phone_number, member_password, member_suggestion_count, kakao_profile_url, member_mentor, member_status, member_common, created_date, updated_date from tbl_member where member_status = 'active' and member_mentor = 'none' and member_common='none'
and ( member_name like concat('%', #{keyword}, '%') or member_email like concat('%', #{keyword}, '%') )
order by id desc limit #{memberCriteria.rowCount} offset #{memberCriteria.offset}

select id, member_name, member_email, kakao_email, member_provider, member_phone_number, member_password, member_suggestion_count, kakao_profile_url, member_mentor, member_status, member_common, created_date, updated_date from tbl_member where member_status = 'active' and member_mentor = 'mentor' and member_common='none'
and ( member_name like concat('%', #{keyword}, '%') or member_email like concat('%', #{keyword}, '%') )
order by id desc limit #{memberCriteria.rowCount} offset #{memberCriteria.offset}

select id, member_name, member_email, kakao_email, member_provider, member_phone_number, member_password, member_suggestion_count, kakao_profile_url, member_mentor, member_status, member_common, created_date, updated_date from tbl_member where member_status = 'active' and member_mentor = 'subscribe' and member_common='none'
and ( member_name like concat('%', #{keyword}, '%') or member_email like concat('%', #{keyword}, '%') )
order by id desc limit #{memberCriteria.rowCount} offset #{memberCriteria.offset}

select id, member_name, member_email, kakao_email, member_provider, member_phone_number, member_password, member_suggestion_count, kakao_profile_url, member_mentor, member_status, member_common, created_date, updated_date
from tbl_member
where kakao_email=#{kakaoEmail}
